import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { uploadDocument } from '../api/documentApi';
import { getAllSubjects } from '../api/subjectApi';
import { AuthContext } from '../context/AuthContext';

const UploadDocument = () => {
  const { currentUser } = useContext(AuthContext);
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    subject: '',
    file: null
  });
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [fileError, setFileError] = useState('');
  
  useEffect(() => {
    // Kiểm tra người dùng đã đăng nhập chưa
    if (!currentUser) {
      navigate('/login');
      return;
    }
    
    // Tải danh sách thể loại
    const fetchSubjects = async () => {
      try {
        const response = await getAllSubjects();
        setSubjects(response.data);
      } catch (err) {
        console.error(err);
      }
    };
    
    fetchSubjects();
  }, [currentUser, navigate]);
  
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };
  
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFileError('');
    
    if (!file) return;
    
    // Kiểm tra kích thước file (tối đa 10MB)
    if (file.size > 10 * 1024 * 1024) {
      setFileError('Kích thước file không được vượt quá 10MB');
      return;
    }
    
    setFormData({
      ...formData,
      file
    });
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (fileError) return;
    if (!formData.file) {
      setFileError('Vui lòng chọn file tài liệu');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      await uploadDocument(formData);
      navigate('/manage-documents');
    } catch (err) {
      setError(err.response?.data?.message || 'Không thể tải lên tài liệu. Vui lòng thử lại sau.');
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="container py-5">
      <h2>Tải lên Tài liệu mới</h2>
      
      {error && <div className="alert alert-danger">{error}</div>}
      
      <div className="card">
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Tiêu đề</label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Mô tả</label>
              <textarea
                className="form-control"
                id="description"
                name="description"
                rows="3"
                value={formData.description}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
            
            <div className="mb-3">
              <label htmlFor="subject" className="form-label">Thể loại</label>
              <select
                className="form-select"
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleInputChange}
                required
              >
                <option value="">-- Chọn thể loại --</option>
                {subjects.map((subject) => (
                  <option key={subject._id} value={subject._id}>
                    {subject.name}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="mb-3">
              <label htmlFor="file" className="form-label">File tài liệu</label>
              <input
                type="file"
                className={`form-control ${fileError ? 'is-invalid' : ''}`}
                id="file"
                onChange={handleFileChange}
                required
              />
              {fileError && <div className="invalid-feedback">{fileError}</div>}
            </div>
            
            <button 
              type="submit" 
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Đang tải lên...' : 'Tải lên'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default UploadDocument;
