// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import { getCurrentUser, login, logout as apiLogout, register } from '../api/authApi';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Khởi tạo auth từ token → lấy user
  useEffect(() => {
    const initAuth = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const resp = await getCurrentUser();
          // nếu API trả về { user } thì resp.user, nếu trả về user trực tiếp thì resp
          const user = resp.user ?? resp;
          setCurrentUser(user);
        }
      } catch (err) {
        console.error('Lỗi xác thực init:', err);
      } finally {
        setLoading(false);
      }
    };
    initAuth();
  }, []);

  const handleLogin = async (credentials) => {
    setError(null);
    try {
      const data = await login(credentials);
      setCurrentUser(data.user);
      localStorage.setItem('token', data.token);
      return data;
    } catch (err) {
      setError(err.response?.data?.message || 'Đăng nhập thất bại');
      throw err;
    }
  };

  const handleRegister = async (userData) => {
    setError(null);
    try {
      const data = await register(userData);
      setCurrentUser(data.user);
      localStorage.setItem('token', data.token);
      return data;
    } catch (err) {
      setError(err.response?.data?.message || 'Đăng ký thất bại');
      throw err;
    }
  };

  const handleLogout = () => {
    apiLogout();
    setCurrentUser(null);
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider
      value={{
        currentUser,
        loading,
        error,
        login: handleLogin,
        register: handleRegister,
        logout: handleLogout,
        isAdmin: currentUser?.role === 'admin',
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
