import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllUsers } from '../api/userApi';
import { getAllDocuments } from '../api/documentApi';
import { AuthContext } from '../context/AuthContext';

// Giá trị mặc định cho thống kê
const defaultStats = {
  users: { total: 0, lastWeek: 0 },
  documents: { total: 0, lastWeek: 0, bySubject: [] },
  subjects: { total: 0 },
  downloads: { total: 0, lastWeek: 0 },
  topUsers: []
};

const Statistics = () => {
  const { currentUser, isAdmin } = useContext(AuthContext);
  const navigate = useNavigate();

  const [stats, setStats] = useState(defaultStats);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Kiểm tra quyền admin
    if (!currentUser || !isAdmin) {
      navigate('/');
      return;
    }

    const fetchStats = async () => {
      try {
        // Lấy danh sách người dùng và tài liệu
        const [usersResponse, documentsResponse] = await Promise.all([
          getAllUsers(),
          getAllDocuments()
        ]);

        const usersData = usersResponse.data;
        const documentsData = documentsResponse.data;

        // Tính số tài liệu đã tải lên cho từng người dùng
        const topUsers = usersData
          .map((user) => {
            const docCount = documentsData.filter(
              (doc) => doc.user === user._id
            ).length;
            return { ...user, documentCount: docCount };
          })
          .sort((a, b) => b.documentCount - a.documentCount)
          .slice(0, 5); // Lấy top 5 người dùng

        setStats((prevStats) => ({
          ...prevStats,
          users: {
            total: usersData.length,
            lastWeek: prevStats.users.lastWeek // Nếu API chưa cung cấp dữ liệu này thì để mặc định
          },
          documents: {
            ...prevStats.documents,
            total: documentsData.length,
            // Dữ liệu theo thể loại có thể được xử lý riêng nếu API trả về; đây là ví dụ giữ nguyên
            bySubject: prevStats.documents.bySubject
          },
          topUsers
        }));
      } catch (err) {
        setError('Không thể tải thống kê. Vui lòng thử lại sau.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [currentUser, isAdmin, navigate]);

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Đang tải...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <h2>Thống kê Hệ thống</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      {/* Các thẻ hiển thị thống kê khác (NGười dùng, Tài liệu, Thể loại, Tải xuống) */}
      <div className="row mb-4">
        <div className="col-md-3 mb-4">
          <div className="card bg-primary text-white">
            <div className="card-body">
              <h5 className="card-title">Tổng số người dùng</h5>
              <h2 className="display-4">{stats.users.total}</h2>
              <p className="card-text">
                <small>+{stats.users.lastWeek} trong tuần qua</small>
              </p>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-success text-white">
            <div className="card-body">
              <h5 className="card-title">Tổng số tài liệu</h5>
              <h2 className="display-4">{stats.documents.total}</h2>
              <p className="card-text">
                <small>+{stats.documents.lastWeek} trong tuần qua</small>
              </p>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-info text-white">
            <div className="card-body">
              <h5 className="card-title">Tổng số thể loại</h5>
              <h2 className="display-4">{stats.subjects.total}</h2>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-4">
          <div className="card bg-warning text-dark">
            <div className="card-body">
              <h5 className="card-title">Lượt tải xuống</h5>
              <h2 className="display-4">{stats.downloads.total}</h2>
              <p className="card-text">
                <small>+{stats.downloads.lastWeek} trong tuần qua</small>
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Hiển thị Top Users */}
      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Người dùng tích cực nhất (Theo số tài liệu tải lên)</h5>
        </div>
        <div className="card-body">
          {stats.topUsers.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>Nickname</th>
                    <th>Tài liệu đã tải lên</th>
                  </tr>
                </thead>
                <tbody>
                  {stats.topUsers.map((user, index) => (
                    <tr key={index}>
                      <td>{user.nickname}</td>
                      <td>{user.documentCount}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="alert alert-info">
              Chưa có dữ liệu thống kê người dùng tích cực.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Statistics;
