// src/components/CommentSection.js
import React, { useState, useEffect, useContext } from 'react';
import {
  getComments,
  createComment,
  updateComment,
  deleteComment,
} from '../api/commentApi';
import { AuthContext } from '../context/AuthContext';

const CommentSection = ({ documentId }) => {
  const { currentUser, loading: authLoading } = useContext(AuthContext);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [editingCommentId, setEditingCommentId] = useState(null);
  const [editingContent, setEditingContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  // Lấy danh sách bình luận cho tài liệu có documentId
  const fetchComments = async () => {
    try {
      setLoading(true);
      const data = await getComments(documentId);
      setComments(data);
    } catch (err) {
      setError('Không thể tải bình luận.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [documentId]);

  // Hàm thêm bình luận mới
  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;
    try {
      const commentData = { document: documentId, content: newComment };
      const addedComment = await createComment(commentData, token);
      setComments([addedComment, ...comments]);
      setNewComment('');
    } catch (err) {
      setError('Không thể thêm bình luận.');
      console.error(err);
    }
  };

  // Hàm cập nhật bình luận của chính user
  const handleUpdateComment = async (e) => {
    e.preventDefault();
    try {
      const updated = await updateComment(editingCommentId, editingContent, token);
      setComments(
        comments.map((c) => (c._id === editingCommentId ? updated : c))
      );
      setEditingCommentId(null);
      setEditingContent('');
    } catch (err) {
      setError('Lỗi khi cập nhật bình luận.');
      console.error(err);
    }
  };

  // Hàm xóa bình luận của chính user
  const handleDeleteComment = async (commentId) => {
    try {
      await deleteComment(commentId, token);
      setComments(comments.filter((c) => c._id !== commentId));
    } catch (err) {
      setError('Lỗi khi xóa bình luận.');
      console.error(err);
    }
  };

  if (authLoading) return <div>Đang kiểm tra quyền người dùng…</div>;

  return (
    <div className="mt-4">
      <h5>Bình luận</h5>
      {error && <div className="alert alert-danger">{error}</div>}

      {/* Form thêm bình luận mới */}
      <form onSubmit={handleAddComment}>
        <div className="mb-3">
          <textarea
            className="form-control"
            placeholder="Nhập bình luận của bạn..."
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Gửi bình luận
        </button>
      </form>

      <hr />

      {loading ? (
        <div>Đang tải bình luận...</div>
      ) : (
        <ul className="list-group">
          {comments.map((comment) => {
            // Nếu comment.user đã populate, sử dụng comment.user._id, nếu không thì dùng trực tiếp comment.user
            const commentOwnerId =
              comment.user && typeof comment.user === 'object'
                ? comment.user._id.toString()
                : comment.user.toString();

            // Nếu comment.user có nickname (đã populate) hiển thị, ngược lại hiển thị "Ẩn danh"
            const displayNickname =
              comment.user && typeof comment.user === 'object' && comment.user.nickname
                ? comment.user.nickname
                : 'Ẩn danh';

            // So sánh currentUser._id với commentOwnerId sau khi chuyển về chuỗi
            const isOwner =
              currentUser &&
              currentUser._id &&
              currentUser._id.toString() === commentOwnerId;

            // Debug: Uncomment dòng sau nếu cần kiểm tra giá trị
            // console.log("currentUser._id:", currentUser._id, "commentOwnerId:", commentOwnerId, "isOwner:", isOwner);

            return (
              <li key={comment._id} className="list-group-item">
                <div>
                  <strong>{displayNickname}</strong>{' '}
                  <small>{new Date(comment.createdAt).toLocaleString('vi-VN')}</small>
                </div>

                {editingCommentId === comment._id ? (
                  <form onSubmit={handleUpdateComment}>
                    <textarea
                      className="form-control"
                      value={editingContent}
                      onChange={(e) => setEditingContent(e.target.value)}
                      required
                    />
                    <div className="mt-2">
                      <button type="submit" className="btn btn-success btn-sm">
                        Lưu
                      </button>
                      <button
                        type="button"
                        className="btn btn-secondary btn-sm ms-2"
                        onClick={() => {
                          setEditingCommentId(null);
                          setEditingContent('');
                        }}
                      >
                        Hủy
                      </button>
                    </div>
                  </form>
                ) : (
                  <p>{comment.content}</p>
                )}

                {isOwner && editingCommentId !== comment._id && (
                  <div className="mt-2">
                    <button
                      className="btn btn-warning btn-sm"
                      onClick={() => {
                        setEditingCommentId(comment._id);
                        setEditingContent(comment.content);
                      }}
                    >
                      Sửa
                    </button>
                    <button
                      className="btn btn-danger btn-sm ms-2"
                      onClick={() => handleDeleteComment(comment._id)}
                    >
                      Xóa
                    </button>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default CommentSection;
