import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Lấy danh sách bình luận theo documentId
export const getComments = async (documentId) => {
  const response = await axios.get(`${API_BASE_URL}/api/comments`, {
    params: { documentId }
  });
  return response.data;
};

// Thêm bình luận mới
export const createComment = async (commentData, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  const response = await axios.post(`${API_BASE_URL}/api/comments`, commentData, config);
  return response.data;
};

// Cập nhật bình luận đã có (chỉ cập nhật nội dung)
export const updateComment = async (commentId, content, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  const response = await axios.put(
    `${API_BASE_URL}/api/comments/${commentId}`,
    { content },
    config
  );
  return response.data;
};

// Xóa bình luận
export const deleteComment = async (commentId, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  const response = await axios.delete(`${API_BASE_URL}/api/comments/${commentId}`, config);
  return response.data;
};
