import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllSubjects, createSubject, updateSubject, deleteSubject } from '../api/subjectApi';
import { getAllDocuments } from '../api/documentApi';
import { AuthContext } from '../context/AuthContext';

const ManageSubjects = () => {
  const { currentUser, isAdmin } = useContext(AuthContext);
  const navigate = useNavigate();
  
  const [subjects, setSubjects] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingSubject, setEditingSubject] = useState(null);
  
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });

  useEffect(() => {
    if (!currentUser || !isAdmin) {
      navigate('/');
      return;
    }

    const fetchData = async () => {
      try {
        const [subjectsResponse, documentsResponse] = await Promise.all([
          getAllSubjects(),
          getAllDocuments()
        ]);
        setSubjects(subjectsResponse.data);
        setDocuments(documentsResponse.data);
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [currentUser, isAdmin, navigate]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (editingSubject) {
        const response = await updateSubject(editingSubject._id, formData);
        setSubjects(subjects.map(subject => subject._id === editingSubject._id ? response.data : subject));
        setEditingSubject(null);
      } else {
        const response = await createSubject(formData);
        setSubjects([...subjects, response.data]);
      }

      setFormData({ name: '', description: '' });
    } catch (err) {
      setError(err.response?.data?.message || 'Không thể lưu thể loại. Vui lòng thử lại sau.');
    }
  };

  const handleEdit = (subject) => {
    setEditingSubject(subject);
    setFormData({
      name: subject.name,
      description: subject.description || ''
    });
  };

  const handleCancelEdit = () => {
    setEditingSubject(null);
    setFormData({ name: '', description: '' });
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa thể loại này?')) {
      return;
    }
    
    try {
      await deleteSubject(id);
      setSubjects(subjects.filter(subject => subject._id !== id));
    } catch (err) {
      setError('Không thể xóa thể loại. Vui lòng thử lại sau.');
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Đang tải...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <h2>{editingSubject ? 'Chỉnh sửa Thể loại' : 'Thêm Thể loại mới'}</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="row">
        <div className="col-md-4">
          <div className="card mb-4">
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Tên thể loại</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="description" className="form-label">Mô tả</label>
                  <textarea
                    className="form-control"
                    id="description"
                    name="description"
                    rows="3"
                    value={formData.description}
                    onChange={handleInputChange}
                  ></textarea>
                </div>

                <div className="d-flex">
                  <button type="submit" className="btn btn-primary me-2">
                    {editingSubject ? 'Cập nhật' : 'Thêm mới'}
                  </button>
                  
                  {editingSubject && (
                    <button type="button" className="btn btn-secondary" onClick={handleCancelEdit}>
                      Hủy
                    </button>
                  )}
                </div>
              </form>
            </div>
          </div>
        </div>

        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Danh sách Thể loại</h5>
            </div>
            <div className="card-body">
              {subjects.length === 0 ? (
                <div className="alert alert-info">
                  Chưa có thể loại nào. Hãy thêm thể loại mới.
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th>Tên thể loại</th>
                        <th>Mô tả</th>
                        <th>Số tài liệu</th>
                        <th>Thao tác</th>
                      </tr>
                    </thead>
                    <tbody>
                      {subjects.map((subject) => {
                        const docCount = documents.filter((doc) => doc.subject === subject._id).length;

                        return (
                          <tr key={subject._id}>
                            <td>{subject.name}</td>
                            <td>{subject.description || '—'}</td>
                            <td>{docCount}</td>
                            <td>
                              <button className="btn btn-sm btn-warning me-2" onClick={() => handleEdit(subject)}>Sửa</button>
                              <button className="btn btn-sm btn-danger" onClick={() => handleDelete(subject._id)}>Xóa</button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageSubjects;
