import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllUsers, deleteUser } from '../api/userApi';
import { getAllDocuments } from '../api/documentApi';
import { AuthContext } from '../context/AuthContext';

const ManageUsers = () => {
  const { currentUser, isAdmin } = useContext(AuthContext);
  const navigate = useNavigate();
  
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Kiểm tra quyền admin
    if (!currentUser || !isAdmin) {
      navigate('/');
      return;
    }
    
    const fetchUsersWithDocs = async () => {
      try {
        // Lấy danh sách user
        const response = await getAllUsers();
        const usersData = response.data;
        
        // Với mỗi user, gọi API lấy danh sách tài liệu của user đó
        const usersWithDocuments = await Promise.all(
          usersData.map(async (user) => {
            try {
              // Giả sử API getAllDocuments chấp nhận filter theo { user: <userID> }
              const docsResponse = await getAllDocuments({ user: user._id });
              // Nếu API trả về danh sách tài liệu (mảng), gắn vào user
              return { ...user, documents: docsResponse.data };
            } catch (docError) {
              console.error(`Error loading documents for user ${user._id}:`, docError);
              return { ...user, documents: [] };
            }
          })
        );

        setUsers(usersWithDocuments);
      } catch (err) {
        setError('Không thể tải danh sách người dùng. Vui lòng thử lại sau.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUsersWithDocs();
  }, [currentUser, isAdmin, navigate]);

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa người dùng này?')) {
      return;
    }
    
    try {
      await deleteUser(userId);
      setUsers(users.filter(user => user._id !== userId));
    } catch (err) {
      setError('Không thể xóa người dùng. Vui lòng thử lại sau.');
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Đang tải...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <h2>Quản lý Người dùng</h2>
      
      {error && <div className="alert alert-danger">{error}</div>}
      
      <div className="card">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Nickname</th>
                  <th>Username</th>
                  <th>Vai trò</th>
                  <th>Ngày đăng ký</th>
                  <th>Tài liệu đã tải lên</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user._id}>
                    <td>{user.nickname}</td>
                    <td>{user.username}</td>
                    <td>
                      <span className={`badge ${user.role === 'admin' ? 'bg-danger' : 'bg-primary'}`}>
                        {user.role}
                      </span>
                    </td>
                    <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                    <td>
                      {user.documents && user.documents.length > 0 ? (
                        <ul className="list-unstyled mb-0">
                          {user.documents.map(doc => (
                            <li key={doc._id}>{doc.title}</li>
                          ))}
                        </ul>
                      ) : (
                        '0'
                      )}
                    </td>
                    <td>
                      {user.role !== 'admin' && (
                        <button 
                          className="btn btn-sm btn-danger" 
                          onClick={() => handleDeleteUser(user._id)}
                        >
                          Xóa
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageUsers;
