// src/components/CommentSection.js
import React, { useState, useEffect, useContext } from 'react';
import {
  getComments,
  createComment,
  updateComment,
  deleteComment,
} from '../api/commentApi';
import { AuthContext } from '../context/AuthContext';

const CommentItem = ({
  comment,
  isEditing,
  editingContent,
  onStartEdit,
  onCancelEdit,
  onChangeEdit,
  onSaveEdit,
  onDelete,
  showActions,
}) => {
  const displayName =
    comment.user && typeof comment.user === 'object' && comment.user.nickname
      ? comment.user.nickname
      : 'Ẩn danh';

  return (
    <li key={comment._id} className="list-group-item">
      <div>
        <strong>{displayName}</strong>{' '}
        <small>{new Date(comment.createdAt).toLocaleString('vi-VN')}</small>
      </div>

      {isEditing ? (
        <form onSubmit={onSaveEdit}>
          <textarea
            className="form-control"
            value={editingContent}
            onChange={onChangeEdit}
            required
          />
          <div className="mt-2">
            <button type="submit" className="btn btn-success btn-sm">Lưu</button>
            <button
              type="button"
              className="btn btn-secondary btn-sm ms-2"
              onClick={onCancelEdit}
            >
              Hủy
            </button>
          </div>
        </form>
      ) : (
        <p>{comment.content}</p>
      )}

      {showActions && !isEditing && (
        <div className="mt-2">
          <button
            className="btn btn-warning btn-sm"
            onClick={onStartEdit}
          >
            Sửa
          </button>
          <button
            className="btn btn-danger btn-sm ms-2"
            onClick={() => onDelete(comment._id)}
          >
            Xóa
          </button>
        </div>
      )}
    </li>
  );
};

const CommentSection = ({ documentId }) => {
  const { currentUser, loading: authLoading } = useContext(AuthContext);

  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [editingCommentId, setEditingCommentId] = useState(null);
  const [editingContent, setEditingContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  // Fetch comments
  const fetchComments = async () => {
    try {
      setLoading(true);
      const data = await getComments(documentId);
      setComments(data);
    } catch (err) {
      setError('Không thể tải bình luận.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [documentId]);

  // Add new comment
  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;
    try {
      const added = await createComment({ document: documentId, content: newComment }, token);
      setComments([added, ...comments]);
      setNewComment('');
    } catch (err) {
      setError('Không thể thêm bình luận.');
      console.error(err);
    }
  };

  // Save edited comment
  const handleSaveEdit = async (e) => {
    e.preventDefault();
    try {
      const updated = await updateComment(editingCommentId, editingContent, token);
      setComments(comments.map(c => c._id === editingCommentId ? updated : c));
      setEditingCommentId(null);
      setEditingContent('');
    } catch (err) {
      setError('Lỗi khi cập nhật bình luận.');
      console.error(err);
    }
  };

  // Delete comment
  const handleDelete = async (id) => {
    try {
      await deleteComment(id, token);
      setComments(comments.filter(c => c._id !== id));
    } catch (err) {
      setError('Lỗi khi xóa bình luận.');
      console.error(err);
    }
  };

  if (authLoading) {
    return <div>Đang kiểm tra quyền người dùng…</div>;
  }

  return (
    <div className="mt-4">
      <h5>Bình luận</h5>
      {error && <div className="alert alert-danger">{error}</div>}

      {/* Form thêm mới */}
      <form onSubmit={handleAddComment}>
        <div className="mb-3">
          <textarea
            className="form-control"
            placeholder="Nhập bình luận của bạn..."
            value={newComment}
            onChange={e => setNewComment(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Gửi bình luận</button>
      </form>

      <hr />

      {loading ? (
        <div>Đang tải bình luận...</div>
      ) : (
        <ul className="list-group">
          {comments.map(comment => {
            // Xác định ID của comment và xem currentUser có phải owner không
            const commentUserId = comment.user && typeof comment.user === 'object'
              ? String(comment.user._id).trim()
              : String(comment.user).trim();

            const isOwner = currentUser && String(currentUser._id).trim() === commentUserId;
            const isEditing = editingCommentId === comment._id;

            return (
              <CommentItem
                key={comment._id}
                comment={comment}
                isEditing={isEditing}
                editingContent={editingContent}
                showActions={isOwner}
                onStartEdit={() => {
                  setEditingCommentId(comment._id);
                  setEditingContent(comment.content);
                }}
                onCancelEdit={() => {
                  setEditingCommentId(null);
                  setEditingContent('');
                }}
                onChangeEdit={e => setEditingContent(e.target.value)}
                onSaveEdit={handleSaveEdit}
                onDelete={handleDelete}
              />
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default CommentSection;
